function InteractiveFeedback({ children }) {
  const [pressed, setPressed] = useState(false);
  const [focused, setFocused] = useState(false);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === ' ') {
      setPressed(true);
    }
  };

  const handleKeyUp = (e) => {
    if (e.key === 'Enter' || e.key === ' ') {
      setPressed(false);
    }
  };

  return (
    <div
      className={cn("interactive-feedback", {
        "scale-press": pressed,
        "hover-lift": !pressed,
        "focus-visible:ring-2 focus-visible:ring-blue-500": focused,
      })}
      tabIndex={0}
      onPointerDown={() => setPressed(true)}
      onPointerUp={() => setPressed(false)}
      onKeyDown={handleKeyDown}
      onKeyUp={handleKeyUp}
      onFocus={() => setFocused(true)}
      onBlur={() => setFocused(false)}
    >
      {children}
    </div>
  );
}

