      // Larger spacing on desktop
      ...(breakpoint === "lg" && {
        [4]: "20px",
        [6]: "28px",
        [8]: "36px",
      }),
    },
  };

  const cssVars = generateCSSCustomProperties(responsiveTokens);
  const root = document.documentElement;
  const originalValues = new Map();

  Object.entries(cssVars).forEach(([key, value]) => {
    originalValues.set(key, root.style.getPropertyValue(key));
    root.style.setProperty(key, value);
  });

  return () => {
    // Cleanup: restore original values
    originalValues.forEach((originalValue, key) => {
      if (originalValue) {
        root.style.setProperty(key, originalValue);
      } else {
        root.style.removeProperty(key);
      }
    });
  };
}, [breakpoint]);

