const COLOR_PATTERNS = [
  /^#[\dA-Fa-f]{3}$/, // #RGB
  /^#[\dA-Fa-f]{6}$/, // #RRGGBB
  /^#[\dA-Fa-f]{8}$/, // #RRGGBBAA
  /^rgb\((?:\s*\d+\s*,){2}\s*\d+\s*\)$/,
  /^rgba\((?:\s*\d+\s*,){3}\s*[\d.]+\s*\)$/,
  /^hsl\(\s*\d+(?:\s*,\s*\d+%?){2}\s*\)$/,
  /^hsla\(\s*\d+(?:\s*,\s*\d+%?){2}\s*,\s*[\d.]+\s*\)$/,
];

function isValidGlassColor(color: string): boolean {
  return COLOR_PATTERNS.some((pattern) => pattern.test(color));
}

