const _useIntersectionObserver = <T extends HTMLElement>(
  callback?: (entry: IntersectionObserverEntry) => void,
  options: IntersectionObserverInit = {},
): [React.RefObject<T>, IntersectionObserverEntry | null] => {
  const [entry, setEntry] = useState<IntersectionObserverEntry | null>(null);
  const elementRef = useRef<T>(null);
  const observerRef = useRef<IntersectionObserver | null>(null);

  const memoizedCallback = useCallback((entry: IntersectionObserverEntry) => {
    if (callback) {
      callback(entry);
    }
  }, [callback]);

  const memoizedOptions = useMemo(() => options, [options.root, options.rootMargin, options.threshold]);

  useEffect(() => {
    // Only run on client side
    if (typeof window === "undefined" || !window.IntersectionObserver) {
      return;
    }

    try {
      // Cleanup previous observer
      if (observerRef.current) {
        observerRef.current.disconnect();
      }

      // Create new observer
      observerRef.current = new IntersectionObserver((entries) => {
        try {
          const [firstEntry] = entries;
          if (firstEntry) {
            setEntry(firstEntry);
            memoizedCallback(firstEntry);
          }
        } catch (error) {
          console.warn("Error in intersection observer callback:", error);
        }
      }, memoizedOptions);

      // Observe element if it exists
      const element = elementRef.current;
      if (element) {
        observerRef.current.observe(element);
      }

      // Cleanup on unmount
      return () => {
        try {
          if (observerRef.current) {
            observerRef.current.disconnect();
          }
        } catch (error) {
          console.warn("Error disconnecting intersection observer:", error);
        }
      };
    } catch (error) {
      console.warn("Error in useIntersectionObserver:", error);
      return;
    }
  }, [memoizedCallback, memoizedOptions]);

  return [elementRef, entry];
};

