const _useOnlineStatus = () => {
  const [isOnline, setIsOnline] = useState(true);

  useEffect(() => {
    if (typeof window === "undefined" || typeof navigator === "undefined") {
      return;
    }

    const updateOnlineStatus = () => {
      setIsOnline(navigator.onLine ?? true);
    };

    updateOnlineStatus();
    window.addEventListener("online", updateOnlineStatus);
    window.addEventListener("offline", updateOnlineStatus);

    return () => {
      window.removeEventListener("online", updateOnlineStatus);
      window.removeEventListener("offline", updateOnlineStatus);
    };
  }, []);

  return isOnline;
};

const _usePageVisibility = () => {
  const [visibility, setVisibility] = useState<"visible" | "hidden">("visible");

  useEffect(() => {
    if (typeof document === "undefined") {
      return;
    }

    const handleVisibilityChange = () => {
      setVisibility(document.hidden ? "hidden" : "visible");
    };

    handleVisibilityChange();
    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => {
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, []);

  return visibility;
};

const _usePerformanceMetrics = () => {
  const [metrics, setMetrics] = useState({
    loadTime: 0,
    renderTime: 0,
    domContentLoaded: 0,
    firstPaint: 0,
    firstContentfulPaint: 0,
  });

  useEffect(() => {
    if (typeof window === "undefined" || !window.performance) {
      return;
    }

    const navigation = window.performance.getEntriesByType("navigation")[0] as PerformanceNavigationTiming;
    const paint = window.performance.getEntriesByType("paint");

    if (navigation) {
      setMetrics({
        loadTime: navigation.loadEventEnd - navigation.loadEventStart,
        renderTime: navigation.domComplete - navigation.domLoading,
        domContentLoaded: navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart,
        firstPaint: paint.find(p => p.name === "first-paint")?.startTime || 0,
        firstContentfulPaint: paint.find(p => p.name === "first-contentful-paint")?.startTime || 0,
      });
    }
  }, []);

  return metrics;
};

