// Render navigation item
const renderNavItem = useCallback((item: NavItem, level = 0) => {
  const isActive = activeItemId === item.id;
  const isExpanded = expandedItems.has(item.id);
  const hasChildren = item.children && item.children.length > 0;

  return (
    <div key={item.id}>
      <button
        type="button"
        onClick={() => handleItemClick(item)}
        disabled={item.disabled}
        className={cn(
          navItemVariants({ size, isActive: isActive ? "true" : "false" }),
          item.disabled && "opacity-50 cursor-not-allowed",
          level > 0 && "ml-4 border-l border-[var(--liquid-glass-border)] pl-4",
        )}
        aria-expanded={hasChildren ? isExpanded : undefined}
        aria-current={isActive ? "page" : undefined}
      >
        {item.icon && (
          <span className="flex-shrink-0 text-[var(--text-secondary)]">
            {item.icon}
          </span>
        )}

        <span className="flex-1 text-left">{item.label}</span>

        {item.badge && (
          <span className="flex h-5 min-w-[20px] items-center justify-center rounded-full bg-blue-500 px-1.5 text-xs font-medium text-white">
            {item.badge}
          </span>
        )}

        {hasChildren && (
          <motion.div
            animate={{ rotate: isExpanded ? 90 : 0 }}
            transition={{ duration: 0.2 }}
            className="flex-shrink-0"
            aria-hidden="true"
          >
            <svg
              className="h-4 w-4 text-[var(--text-secondary)]"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M9 5l7 7-7 7"
              />
            </svg>
          </motion.div>
        )}
      </button>

      {/* Render children */}
      <AnimatePresence>
        {hasChildren && isExpanded && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: "auto", opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.2 }}
            className="overflow-hidden"
          >
            <div className="py-1">
              {item.children?.map((child) =>
                renderNavItem(child, level + 1),
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}, [activeItemId, expandedItems, handleItemClick, size]);

