const { filteredItems, categorizedItems, allFilteredItems } = useMemo(() => {
  const filtered = items.filter((item) => {
    const searchTerms = query.toLowerCase().split(" ");
    const itemText =
      `${item.label} ${item.description || ""} ${item.keywords?.join(" ") || ""}`.toLowerCase();
    return searchTerms.every((term) => itemText.includes(term));
  });

  const categorized = filtered.reduce(
    (accumulator, item) => {
      const category = item.category || "General";
      if (!accumulator[category]) {
        accumulator[category] = [];
      }
      accumulator[category].push(item);
      return accumulator;
    },
    {} as Record<string, Array<CommandItem>>,
  );

  const allFiltered = Object.values(categorized).flat();

  return {
    filteredItems: filtered,
    categorizedItems: categorized,
    allFilteredItems: allFiltered
  };
}, [items, query]);

