const getInitials = (name: string) => {
  if (!name || name.trim().length === 0) return "?";
  return name
    .trim()
    .split(" ")
    .filter(word => word.length > 0)
    .map((word) => word[0])
    .join("")
    .slice(0, 2)
    .toUpperCase();
};

fix.content:
const getInitials = (name: string) => {
  if (!name || name.trim().length === 0) return "?";
  return name
    .trim()
    .split(" ")
    .filter(word => word.length > 0)
    .map((word) => word[0])
    .join("")
    .slice(0, 2)
    .toUpperCase();
};

